mapper.update:
  update  tb_user_category
  set usetype =  #{usetype}
		WHERE sortid LIKE CONCAT(#{sortid},'%')
		AND category_type = #{categoryType}
		AND user_id = #{userId}

mapper.select:
  		select * from tb_user_category 
  		where (user_id = #{uid} or user_id = 0) 
		and parent_id=#{pid}
		AND category_type = #{categoryType}
		order by sortId

mapper.update:
   UPDATE tb_knowledge_category SET STATUS=1
   WHERE knowledge_id IN 
    
   		#{id}
