mapper.update:
UPDATE tb_column_visible t SET t.state= #{state} ,t.utime=#{utime}
WHERE t.user_id=#{userId} and t.pcid=#{pcid}

	and t.column_id in
	
		#{tag}
	


	and t.column_id not in
	
		#{tag}

mapper.select:
select column_id from  tb_column_visible t where  t.user_id=#{userId} and t.pcid=#{pcid}
and t.column_id not in
	
		#{tag}
