mapper.insert:
insert into tb_user_permission ( receive_user_id, knowledge_id, send_user_id, type, mento, createtime, column_id
) values
(#{item.receiveUserId}, #{item.knowledgeId}, #{item.sendUserId}, #{item.type}, #{item.mento}, #{item.createtime}, #{item.columnId})

mapper.select:
select knowledge_id from tb_user_permission
where 1=1
		
	and receive_user_id=#{receive_user_id} 

		
	and type=#{type} 

		
	and column_id=#{column_id}

select knowledge_id from tb_user_permission
where (1=1 

	and ((receive_user_id=#{receive_user_id} or send_user_id=#{receive_user_id})and type=3) 

	or type=4 
	)

	and column_id=#{column_id}

mapper.delete:
	 delete from tb_user_permission 
    where  send_user_id=#{userid} 
    and  knowledge_id in 
   
  #{knowledgeids}

